import React, { useState } from 'react';
import './App.css';

function App() {
  const [options, setOptions] = useState({
    text: '', 
    count: 0 
  });

  const handleVote = (index) => {
    const newOptions = [...options];
    newOptions[index].count += 1;
    setOptions(newOptions);
  };

  return (
    <div className="App">
      <h1>Vote App</h1>
      <div className="container">
        <ul>
          {options.map((option, index) => (
            <li key={index}>
              <button className='voteBtn' onClick={() => handleVote(index)}>{option.text}</button>
              <br/>
              <span>Count: {option.count}</span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
