import './App.css';
import { BrowserRouter as Router, Link, Route, Routes } from 'react-router-dom';
import { useState } from 'react';

function App() {
  const [messages, setMessages] = useState([
    { id: 1, writer: '작성자1', message: '메시지1', writer_date: '2022-01-01' },
    { id: 2, writer: '작성자2', message: '메시지2', writer_date: '2022-02-01' },
    { id: 3, writer: '작성자3', message: '메시지3', writer_date: '2022-03-01' },
  ]);
  const [newMessage, setNewMessage] = useState({
    id: '',
    writer: '',
    message: '',
    writer_date: '',
  });

  const HandleAddMessage = () => {
    if (!newMessage.id || !newMessage.writer || !newMessage.message || !newMessage.writer_date) {
      alert('입력되지 않은 항목이 있습니다. 다시 입력해주세요.');
      return;
    }
    const isDuplicate = messages.some((message) => String(message.id) === newMessage.id);
    if (isDuplicate) {
      alert('이미 사용 중인 ID입니다.');
      return;
    }
    setMessages([...messages, newMessage]);
    setNewMessage({ id: '', writer: '', message: '', writer_date: '' });
  };

  const HandelInputChange = (event) => {
    const { name, value } = event.target;
    setNewMessage({ ...newMessage, [name]: value });
  };

  const HandleDeleteMessages = (id) => {
    const updatedMessages = messages.filter((message) => message.id !== id);
    setMessages(updatedMessages);
  };

  return (
    <Router>
      <div className='container'>
        <nav>
          <ul>
            <li>
              <Link to='/'>목록</Link>
            </li>
            <li>
              <Link to='/CreateMessage'>새 메시지 추가</Link>
            </li>
          </ul>
        </nav>
        <Routes>
          <Route path='/' element={<Home messages={messages} onDeleteMessages={HandleDeleteMessages} />} />
          <Route
            path='/CreateMessage'
            element={<CreateMessage newMessage={newMessage} onAddMessage={HandleAddMessage} onInputChange={HandelInputChange} />}
          />
        </Routes>
      </div>
    </Router>
  );
}

const Home = (props) => {
  const { messages, onDeleteMessages } = props;

  return (
    <div>
      <h1>메시지 목록</h1>
      <form>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>작성자</th>
              <th>메시지 내용</th>
              <th>작성일</th>
              <th>동작</th>
            </tr>
          </thead>
          <tbody>
            {messages.map((message) => (
              <tr key={message.id}>
                <td>{message.id}</td>
                <td>{message.writer}</td>
                <td>{message.message}</td>
                <td>{message.writer_date}</td>
                <td>
                  <button onClick={() => onDeleteMessages(message.id)}>삭제</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </form>
    </div>
  );
};

const CreateMessage = (props) => {
  const { newMessage, onAddMessage, onInputChange } = props;

  return (
    <div>
      <h1>새 메시지 작성</h1>
      <form>
        <input
          type='number'
          name='id'
          value={newMessage.id}
          onChange={onInputChange}
          placeholder='메시지 ID 입력'
        />
        <br />
        <input
          type='text'
          name='writer'
          value={newMessage.writer}
          onChange={onInputChange}
          placeholder='작성자 입력'
        />
        <br />
        <input
          type='text'
          name='message'
          value={newMessage.message}
          onChange={onInputChange}
          placeholder='메시지 내용 입력'
        />
        <br />
        <label>작성일 : </label>
        <input type='date' name='writer_date' value={newMessage.writer_date} onChange={onInputChange} />
        <br />
      </form>
      <button onClick={onAddMessage}>메시지 추가</button>
    </div>
  );
};

export default App;
