import React, { useEffect } from 'react';
import 'react-summernote/dist/react-summernote.css';
import 'bootstrap/dist/css/bootstrap.css';
import $ from 'jquery';
import 'bootstrap/js/modal';
import 'bootstrap/js/dropdown';
import 'bootstrap/js/tooltip';
import 'bootstrap/js/popover';
import 'bootstrap/js/transition';
import 'bootstrap/js/collapse';
import 'bootstrap/js/tab';
import 'bootstrap/js/alert';
import 'bootstrap/js/button';
import 'bootstrap/js/carousel';

import 'react-summernote/lang/summernote-ko-KR'; // 선택적으로 한국어 언어 설정

import 'react-summernote/dist/react-summernote.js';

function App() {
  useEffect(() => {
    $('#summernote').summernote(); // Summernote 초기화

    const inputFile = document.getElementById("file");
    const video = document.getElementById("video");

    inputFile.addEventListener("change", function(){
      const file = inputFile.files[0];
      const videourl = URL.createObjectURL(file);
      video.setAttribute("src", videourl);
      video.play();
    });
  }, []);

  return (
    <div className="App">
      <div id="summernote"><p>Hello Summernote</p></div>
      <div className="ac-input-with-item e-input-tags ac-input-tags ac-input-tags--blue">
        <button className="btnTest "><span className="ac-tag__name">자바</span><svg width="6" height="6" className="tag___close-btn" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 12 12"><path fill="#495057" fill-rule="evenodd" d="M.203.203c.27-.27.708-.27.979 0L6 5.02 10.818.203c.27-.27.709-.27.98 0 .27.27.27.708 0 .979L6.978 6l4.818 4.818c.27.27.27.709 0 .98-.27.27-.709.27-.979 0L6 6.978l-4.818 4.818c-.27.27-.709.27-.98 0-.27-.27-.27-.709 0-.979L5.022 6 .203 1.182c-.27-.27-.27-.709 0-.98z" clip-rule="evenodd"></path></svg></button>
        <input value="" type="text" spellcheck="false" theme="blue" placeholder="태그로 검색해보세요!" initial-value="자바" />
        <input id="file" type="file" accept="video/mp4,video/mkv, video/x-m4v,video/*" />
        <video id="video" controls></video>
      </div>
    </div>
  );
}

export default App;
