import './App.css';
import {BrowserRouter as Router, Link, Routes, Route} from 'react-router-dom';
import {useState} from 'react';
import Home from './Home';
import CreateMovie from './CreateMovie';

function App() {
  // 영화 게시판의 기본데이터 + ,setter 함수
  const [movies, setMovies] = useState([
    { id : 1, title : '오펜하이머', genre : '서사', release_date : '2023-08-17'},
    { id : 2, title : '콘크리트유토피아', genre : '스릴러', release_date : '2023-08-10'},
    { id : 3, title : '엘리먼트', genre : '판타지', release_date : '2023-07-15'},
  ]);
  // 새로운 영화 정보를 저장할 객체 -> CreateMovie 컴포넌트에 넘겨줄 예정
  const [newMovie, setNewMovie] = useState({
    id : '',
    title : '',
    genre : '',
    release_date : ''
  });

  // CreateMovie 컴포넌트의 각 인풋태그에 추가할 이벤트 핸들러
  // input 태그의 값이 바뀔때마다 (change) newMovie객체에 각 프로퍼티에 변경된 값들로 초기화 시킬 예정
  // ex) 영화의 제목(title)값 수정을 한다면 newMovie의 title프로퍼티에 값을 변경시켜주는 기능 (부가적인 기능이다)
  const handleInputChange = (event) =>{
    const {name, value} = event.target;
    setNewMovie({...newMovie, [name] : value});
  }

  // 영화 등록 메서드 -> 요효성 검사를 통과하면 setMovie함수를 호출하여 movies 상태값을 변경해줌4

  // 영화 삭제 메서드 -> 투두리스트와 동일
  const handleDeleteMovie = (id) => {
    const updatedMovies = movies.fileter( (movie) => movie.id !== id );
    setMovies(updatedMovies);
  }
  return (
   <Router>
    <div className='container'>
      <nav>
        <ul>
          <li>
            <Link to="/">List</Link>
          </li>
          <li>
            <Link to="/create">Add New Movie</Link>
          </li>
        </ul>
      </nav>
      <Routes>
        <Route path="/" element={<Home movies={movies} onDeleteMovie={handleDeleteMovie}/>}/>
        <Route path="/create" element={<CreateMovie />}/>
      </Routes>
    </div>
   </Router>
  );
}

export default App;
