import React, { useEffect } from 'react';
import 'react-summernote/dist/react-summernote.css';
import 'bootstrap/dist/css/bootstrap.css';
import $ from 'jquery';
import 'bootstrap/js/modal';
import 'bootstrap/js/dropdown';
import 'bootstrap/js/tooltip';
import 'bootstrap/js/popover';
import 'bootstrap/js/transition';
import 'bootstrap/js/collapse';
import 'bootstrap/js/tab';
import 'bootstrap/js/alert';
import 'bootstrap/js/button';
import 'bootstrap/js/carousel';

import 'react-summernote/lang/summernote-ko-KR'; // 선택적으로 한국어 언어 설정

import 'react-summernote/dist/react-summernote.js';

function App() {
  useEffect(() => {
    $('#summernote').summernote(); // Summernote 초기화

    const inputFile = document.getElementById("file");
    const video = document.getElementById("video");

    inputFile.addEventListener("change", function(){
      const file = inputFile.files[0];
      const videourl = URL.createObjectURL(file);
      video.setAttribute("src", videourl);
      video.play();
    });
  }, []);

  return (
    <div className="App">
        <input id="file" type="file" accept="video/mp4,video/mkv, video/x-m4v,video/*" />
        <video id="video" controls></video>
      </div>
  );
}

export default App;
