import React, { useState } from 'react';
import { BrowserRouter as Router, Link, Route, Routes } from 'react-router-dom';
import './App.css';

function App() {
  const [options, setOptions] = useState([]);

  const handleAddVote = (newOptions) => {
    setOptions(newOptions);
  };

  return (
    <Router>
      <div className='votewrap'>
        <nav>
          <ul>
            <li>
              <Link to='/'>Vote App</Link>
            </li>
            <li>
              <Link to='/create'>Add New Vote</Link>
            </li>
          </ul>
        </nav>
        <Routes>
          <Route
            path='/'
            element={<Home options={options} setOptions={setOptions} />}
          />
          <Route path='/create' element={<CreateVote onAddVote={handleAddVote} />} />
        </Routes>
      </div>
    </Router>
  );
}

const Home = (props) => {
  const { options, setOptions } = props;

  const handleVote = (id) => {
    const newOptions = options.map((option) =>
      option.id === id ? { ...option, count: option.count + 1 } : option
    );
    setOptions(newOptions);
  };

  return (
    <div className='App'>
      <h1>Vote App</h1>
      <div className='container'>
        <ul>
          {options.map((option, index) => (
            <li key={index}>
              <button className='voteBtn' onClick={() => handleVote(option.id)}>
                {option.text}
              </button>
              <br />
              <span>Count: {option.count}</span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

const CreateVote = (props) => {
  const { onAddVote } = props;
  const [newOptions, setNewOptions] = useState([
    { id: 1, text: '', count: 0 },
    { id: 2, text: '', count: 0 },
  ]);

  const [editorContent, setEditorContent] = useState(''); // editorContent 상태 추가

  const handleInputChange = (event, index) => {
    const { name, value } = event.target;
    setNewOptions((prevOptions) => {
      const newOption = { ...prevOptions[index], [name]: value };
      const updatedOptions = [...prevOptions];
      updatedOptions[index] = newOption;
      return updatedOptions;
    });
  };

  const handleEditorChange = (evt) => {
    const content = evt.editor.getData(); // CKEditor 내용을 가져옴
    setEditorContent(content); // editorContent 상태 업데이트
  };

    const handleFontStyleChange = (style) => {
    // 에디터 내용에 선택한 폰트 스타일을 적용하는 로직
    const newContent = `<span style="font-family: ${style}">${editorContent}</span>`;
    setEditorContent(newContent);
  };


  const handleAddVote = () => {
    const validOptions = newOptions.filter((option) => option.text !== '');
    if (validOptions.length !== 2) {
      alert('두 개의 투표 옵션을 입력해주세요.');
      return;
    }

    onAddVote(validOptions);
    setNewOptions([{ id: 1, text: '', count: 0 }, { id: 2, text: '', count: 0 }]);
  };


  return (
    <div>
      <h1>Create Vote</h1>
      <form>
        {newOptions.map((option, index) => (
          <div key={index}>
            <input
              type='text'
              name='text'
              value={option.text}
              onChange={(e) => handleInputChange(e, index)}
              placeholder={`투표 옵션 ${index + 1} 입력`}
            />
          </div>
        ))}
         <textarea
          id='editor'
          value={editorContent}
          onChange={(e) => setEditorContent(e.target.value)}
        ></textarea>
        <script src='https://cdn.ckeditor.com/4.16.2/standard/ckeditor.js'></script>
        <script>
          {`
          CKEDITOR.replace('editor');
          CKEDITOR.editorConfig = function( config ) {
            config.filebrowserUploadUrl = '/upload'; // 이미지 업로드 URL 설정
          };
          `}
        </script>
      </form>
      <button onClick={handleAddVote}>Add Vote</button>
    </div>
  );
};

export default App;
