import './App.css';
import { BrowserRouter as Router, Link, Route , Routes  } from 'react-router-dom';
import { useState } from 'react';
import Home from './Home.js';
import CreateMovie from './CreateMovie';

function App() {
  const [movies, setMovies] = useState([
    { id : 1, title : '오펜하이머', genre : '서사', release_date : '2023-08-17'},
    { id : 2, title : '콘크리트유토피아', genre : '스릴러', release_date : '2023-08-10'},
    { id : 3, title : '엘리먼트', genre : '판타지', release_date : '2023-07-15'},
  ]);
  const [newMovie, setNewMovie] = useState({
    id : '',
    title : '',
    genre : '',
    release_date : ''
  });
  const HandleDeleteMovie = (id) =>{
    const updateMovie = movies.filter( (movie)=> movie.id !== id);
    setMovies(updateMovie);
  }
  const HandleAddMovie = ()=>{
    if(!newMovie.id || !newMovie.title || !newMovie.genre || !newMovie.release_date){
      alert("모든 값을 바르게 입력해주세요");
      return;
    }
    const isDuplicate = movies.some( (movie)=> String(movie.id) === newMovie.id );
    if(isDuplicate){
      alert("이미 존재하는 ID입니다. 다시 입력해주세요");
      setNewMovie({...newMovie, ['id']: ''});
      return;
    }
    setMovies([...movies, newMovie]);
    setNewMovie({ id:'', title:'', genre:'', release_date:''});
  }
  const HandelInputChange = (event)=>{
    const {name, value} = event.target;

  }


  return (
    <Router>
      <div className='container'>
        <nav>
          <ul>
            <li>
              <Link to="/">List</Link>
            </li>
            <li>
              <Link to="/CreateMovie">Add New Movie</Link>
            </li>
          </ul>
        </nav>
        <Routes>
          <Route path="/" element={<Home  movies={movies} onDeleteMovie={HandleDeleteMovie}/>}/>
          <Route path="/CreateMovie" element={<CreateMovie newMovie={newMovie} onAddMovie={HandleAddMovie} />}/>
        </Routes>

      </div>
    </Router>

  );
}

export default App;
