import React, { useState } from 'react';
import { BrowserRouter as Router, Link, Route, Routes } from 'react-router-dom'; // 이 줄 추가
import './App.css';

function App() {
  const [options, setOptions] = useState([]);

  const handleAddVote = (newOption) => {
    setOptions([newOption]);
  };

  return (
    <Router> {/* 이 줄 수정 */}
      <div className='container'>
        <nav>
          <ul>
            <li>
              <Link to='/'>Vote App</Link>
            </li>
            <li>
              <Link to='/create'>Add New Vote</Link>
            </li>
          </ul>
        </nav>
        <Routes>
          <Route path='/' element={<Home options={options} />} />
          <Route
            path='/create'
            element={<CreateVote onAddVote={handleAddVote} />}
          />
        </Routes>
      </div>
    </Router>
  );
}

const CreateVote = (props) => {
  const { onAddVote } = props;
  const [newOption, setNewOption] = useState({
    text: '',
    count: 0,
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setNewOption((prevOption) => ({
      ...prevOption,
      [name]: value,
    }));
  };

  const handleAddVote = () => {
    if (!newOption.text) {
      alert('투표 옵션을 입력해주세요.');
      return;
    }

    onAddVote(newOption);
    setNewOption({ text: '', count: 0 });
  };

  return (
    <div>
      <h1>Create Vote</h1>
      <form>
        <input
          type='text'
          name='text'
          value={newOption.text}
          onChange={handleInputChange}
          placeholder='투표 옵션 입력'
        />
      </form>
      <button onClick={handleAddVote}>Add Vote</button>
    </div>
  );
};

export default App;
