  return (
    <div  className="flex flex-col items-center justify-center mt-16">
      <h1 className="text-5xl">Clock</h1>
      <p className="mt-4 text-3xl">{today.toLocaleTimeString()}</p>
      <p className="mt-4 text-2xl">{today.toLocaleDateString()}</p>

    </div>
  )
}
